import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import API from '../api';
import Navbar from '../components/Navbar';
import '../styles/ItemDetail.css';

function ItemDetail() {
  const { id } = useParams();
  const [item, setItem] = useState(null);

  useEffect(() => {
    async function fetchItem() {
      try {
        const res = await API.get(`/items/${id}`);
        setItem(res.data);
      } catch (err) {
        console.error('Failed to load item:', err);
      }
    }

    fetchItem();
  }, [id]);

  if (!item) return <p>Loading...</p>;

  return (
    <div className="item-detail-wrapper">
      <Navbar />
      <div className="item-detail-content"></div>
      <div className="item-detail-card">
        <img
          className="item-detail-img"
          src={`http://localhost:3000/${item.imagePath}`}
          alt={item.name}
        />
        <div className="item-detail-info">
          <h1>{item.name}</h1>
          <p className="description">{item.description}</p>
          <p><strong>Category:</strong> {item.category}</p>
          {item.subcategory && <p><strong>Subcategory:</strong> {item.subcategory}</p>}
          <p><strong>Condition:</strong> {item.condition}</p>
          {item.size && <p><strong>Size:</strong> {item.size}</p>}
          <p><strong>Status:</strong> {item.status}</p>
          {item.status === 'borrow' && item.washInstructions && (
            <p><strong>Wash Instructions:</strong> {item.washInstructions}</p>
          )}
          {item.status === 'buy' && item.price && (
            <p><strong>Price:</strong> ${item.price}</p>
          )}
          <hr />
          <p><strong>Owner:</strong> {item.owner?.name}</p>
        </div>
      </div>
    </div>
  );
}

export default ItemDetail;
